  ]
};

// Get input data from previous node
const inputData = $input.all()[0].json;
const topic = inputData.originalPrompt || inputData.body?.originalPrompt || 'the topic';
const baseContent = inputData.generatedContent || inputData.body?.generatedContent || 'the content';

// Generate release prompts by replacing placeholders
const generatePrompts = (prompts, topic, baseContent) => {
  return prompts.map(prompt => ({
    ...prompt,
    prompt: prompt.template
      .replace(/\{topic\}/g, topic)
      .replace(/\{baseContent\}/g, baseContent)
  }));
};

const releasePrompts = {
  textPrompts: generatePrompts(templateData.structure.textPrompts, topic, baseContent),
  imagePrompts: generatePrompts(templateData.structure.imagePrompts, topic, baseContent),
  videoPrompts: generatePrompts(templateData.structure.videoPrompts, topic, baseContent),
  marketingPrompts: generatePrompts(templateData.structure.marketingPrompts, topic, baseContent),
  usageTips: templateData.usageTips
};

// Return the enhanced data with release prompts
return {
  ...inputData,
  releasePrompts: releasePrompts,
  timestamp: new Date().toISOString(),
  status: 'release_prompts_generated'
};

